from typing import List

class Solution:

    def maxAreaOfIsland(self, grid: List[List[int]]) -> int:
        """Return the size of the largest 'island' of 1's in grid."""

        m, n = len(grid), len(grid[0])
        visited = set()

        def counting_dfs(x, y):
            """Do DFS while counting number of elements."""

            area = 1
            dfs_stack = [(x, y)]
            visited.add((x, y))

            while dfs_stack:
                curr_x, curr_y = dfs_stack.pop()
                neighbors = [
                    (curr_x + 1, curr_y),
                    (curr_x - 1, curr_y),
                    (curr_x, curr_y + 1),
                    (curr_x, curr_y - 1)
                ]

                for nb in neighbors:
                    in_range = (0 <= nb[0] < m) and (0 <= nb[1] < n)
                    if in_range and grid[nb[0]][nb[1]] == 1 and (nb not in visited):
                        area += 1
                        visited.add(nb)
                        dfs_stack.append(nb)
            
            return area
        
        max_area = 0

        for i in range(m):
            for j in range(n):
                if (i, j) not in visited and grid[i][j] == 1:
                    max_area = max(max_area, counting_dfs(i, j))
        
        return max_area
    


